import { AbstractControl, ValidationErrors, ValidatorFn } from "@angular/forms";
import { Observable } from "rxjs";

export const checkPasswordsValidator: ValidatorFn = (group: AbstractControl):  Observable<ValidationErrors | null>=> { 
    let password = group.get('password')?.value;
    let confirmPassword = group.get('conformPassword')?.value;
    return password === confirmPassword ? null : { passNotMatch: true }
  }
