import { CommonModule } from '@angular/common';
import { Component, Inject, inject } from '@angular/core';
import { AbstractControlOptions, FormBuilder, FormGroup, ReactiveFormsModule, Validators } from '@angular/forms';
import { UserService } from '../../../services/user.service';
import { MessageService } from 'primeng/api';
import { Router } from '@angular/router';
import { DuplicateUserDataError } from '../../../exceptions/DuplicateUserDataError';
import { UserRegisterData } from '../../../models/user/userRegisterData';
import { checkPasswordsValidator } from '../../validators/checkPasswords.validator';
import { userLoginData } from '../../../models/user/userLoginData';


@Component({
  selector: 'app-register',
  standalone: true,
  imports: [CommonModule, ReactiveFormsModule],
  templateUrl: './register.component.html',
  styleUrls: ['./register.component.css'],

})
export class RegisterComponent {
  registerForm=this.formBuilder.group({
    nickName: ['', Validators.required],
    email: ['', [Validators.email,Validators.required]],
    password: ['', [Validators.required, Validators.minLength(8)]],
    confirmPassword: ['', [Validators.required, Validators.minLength(8)]]
    },{validators: checkPasswordsValidator, updateOn: 'submit'}  as AbstractControlOptions)

  router: Router = inject(Router);

  constructor(private formBuilder: FormBuilder, private userService: UserService, private messageService: MessageService){}


  registerUser() {
    if (this.registerForm.invalid){
      this.messageService.add({severity: 'error', summary: 'Error', detail: 'Incorrect register data!'});
      return;
    }

    const registerData = {
      nickName: this.registerForm.get('nickName')?.value,
      email: this.registerForm.get('email')?.value,
      password: this.registerForm.get('password')?.value,
      confirmPassword: this.registerForm.get('confirmPassword')?.value

    } as UserRegisterData;

    this.userService.register(registerData).subscribe(
      {
      next: () => {
        const loginData = {
          email: this.registerForm.get('email')?.value,
          password: this.registerForm.get('password')?.value
        } as userLoginData

        this.userService.login(loginData).subscribe(
          {
            next: () => {
              this.router.navigate(['home']);
            }
          }
        );

      },
      error: (e) =>{
        if (e instanceof DuplicateUserDataError){
          this.messageService.add({severity: 'error', summary: 'Błąd', detail: 'Registration failed, the user already exists.'});
          this.registerForm.reset();
        }
        else
        {
          this.messageService.add({severity: 'error', summary: 'Error', detail: 'Server connection error'})
        }
      }

    });
  }
}
<div class = "form">
    <form [formGroup]="registerForm" (ngSubmit)="registerUser()" errorTailor>
        <div >
            <label>NickName: <input formControlName="nickName" type="text" pInputText placeholder="nickName"></label>
        </div>
        <div class="error-messages error" *ngIf="registerForm.controls.nickName.invalid && (registerForm.controls.nickName.dirty || registerForm.controls.nickName.touched)">
            <small *ngIf="registerForm.controls.nickName.errors?.['required']">
                NickName is required.
            </small>
        </div>
        <div >
            <label>Email: <input formControlName="email" type="text" pInputText placeholder="Email"></label>
        </div>
        <div class="error-messages error" *ngIf="registerForm.controls.email.invalid && (registerForm.controls.email.dirty || registerForm.controls.email.touched)"> 
            <small *ngIf="registerForm.controls.email.errors?.['email']">
                Enter valid email.
            </small>
            <small *ngIf="registerForm.controls.email.errors?.['required']">
                Email is required.
            </small>
        </div>
        <div>
            <label>Password: <input formControlName="password" type="password" pInputText placeholder="Password"></label>
        </div>
        <div class="error-messages error" *ngIf="registerForm.controls.password.invalid && (registerForm.controls.password.dirty || registerForm.controls.password.touched)"> 
            <small *ngIf="registerForm.controls.password.errors?.['required']">
                Password is required.
            </small>
            <small *ngIf="registerForm.controls.password.errors?.['minlength']">
                Passwords myst be at least 8 characters long.
            </small>
        </div>
        <div>
            <label>Confirm Password: <input formControlName="confirmPassword" type="password" pInputText placeholder="Confirm Password"></label>
        </div>
        <div class="error-messages error" *ngIf="registerForm.controls.confirmPassword.invalid && (registerForm.controls.confirmPassword.dirty || registerForm.controls.confirmPassword.touched)"> 
            <small *ngIf="registerForm.controls.confirmPassword.errors?.['required']">
                Confirm Password is required.
            </small>
            <small *ngIf="registerForm.controls.confirmPassword.errors?.['passNotMatch']">
                Passwords are diffrent.
            </small>
        </div>
        <div>
            <small *ngIf="registerForm.errors?.['passNotMatch']">
                Passwords are diffrent.
            </small> 
        </div>  
        <button type="submit">Register</button>
    </form> 
</div>
